import React, {useEffect, useState} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';

// Imports Axios to define DB depending on enviroment
import axiosClient from './config/axios';

// Imports components
import Patients from './components/Patients';
import NewRecord from './components/NewRecord';
import Record from './components/Record';




function App() {
  // Sets states for the app
  const [record, saveRecord] = useState([]);

  // Defines what to do when a React component is reloaded
  useEffect(() => {
    const apiInit = () => {
      axiosClient.get('/patients')
      .then(res => 
        // set response as state
        saveRecord(res.data)
      )
      .catch(error => {
        console.log(error);
      })
    }
    apiInit();
  }, [] );

  return (
    <Router>
      <Switch>

        <Route exact path="/" component={() => <Patients/>}/>

        <Route exact path="/new" component={NewRecord}/>

        <Route exact path="/new" component={NewRecord}/>

        <Route exact path="/new/:id" component={Record}/>

      </Switch>
    </Router>
  );
}

export default App;
